            const targetIndustries = ['Food & Beverage', 'Manufacturing', 'Retail', 'Technology', 'Healthcare', 'Agriculture', 'Logistics', 'Packaging', 'Hospitality', 'Consumer Goods', 'Chemicals', 'Pharmaceuticals', 'Automotive', 'Energy', 'Construction', 'Transportation', 'Finance', 'Education', 'Media & Entertainment', 'Telecommunications'];
            const targetCountries = ['Thailand', 'Vietnam', 'Malaysia', 'Indonesia', 'Singapore', 'Philippines', 'China', 'India', 'Japan', 'South Korea', 'Australia', 'New Zealand', 'United States', 'Canada', 'Mexico', 'Brazil', 'Argentina', 'Colombia', 'Peru', 'Chile', 'United Kingdom', 'France', 'Germany', 'Italy', 'Spain', 'Russia', 'Saudi Arabia', 'United Arab Emirates', 'South Africa', 'Nigeria'];
            const targetKeywords = ['Noodles', 'Food Processing', 'Packaging', 'Organic', 'Healthy', 'Sustainability', 'Seafood', 'Bakery', 'Confectionery', 'Dairy', 'Plant-based', 'Coffee', 'Vertical Farming', 'Craft Beer', 'Snacks', 'Palm Oil', 'Logistics', 'Meal Delivery', 'Poultry', 'Chocolate', 'Beverage', 'Grocery', 'Import', 'Export', 'Automation', 'Innovation', 'Distribution', 'Supply Chain', 'Retail', 'Branding'];

            const randomIndex = Math.floor(Math.random() * projectNames.length);

            $('#project-name').val(projectNames[randomIndex]);
            $('#project-description').val(projectDescriptions[randomIndex]);
            $('#hub').val('2').trigger('change');
            $('#deadline').val(getRandomFutureDate());
            $('#target_company_size').val(targetCompanySizes[Math.floor(Math.random() * targetCompanySizes.length)]).trigger('change');

            const randomLanguages = getRandomElements(communicationLanguages, 2, 4);
            $('#communication_language').val(randomLanguages).trigger('change');

            $('#target_industry').val(Math.floor(Math.random() * targetIndustries.length)).trigger('change');

            const randomCountries = getRandomElements(targetCountries, 1, 5);
            $('#target_country').val(randomCountries.map((country, index) => index)).trigger('change');

            const randomKeywords = getRandomElements(targetKeywords, 1, 10);
            $('#target_keyword').data('tagify').addTags(randomKeywords);

            $('#q1').val('What is the main key products your company sells?');
        }

        function getRandomFutureDate() {
            const today = new Date();
            const futureDate = new Date(today.getTime() + Math.random() * 365 * 24 * 60 * 60 * 1000);
            const year = futureDate.getFullYear();
            const month = String(futureDate.getMonth() + 1).padStart(2, '0');
            const day = String(futureDate.getDate()).padStart(2, '0');
            return `${day}/${month}/${year}`;
        }

        function getRandomElements(array, minCount, maxCount) {
            const count = Math.floor(Math.random() * (maxCount - minCount + 1)) + minCount;
            const shuffledArray = array.slice().sort(() => 0.5 - Math.random());
            return shuffledArray.slice(0, count);
        }

        tagifyInit();

        function tagifyInit() {
            $('#target_keyword').tagify({
                maxTags: 20,
            });
        }

        $('#select_company').on('select2:select', function(e) {
            $('#company_holder').removeClass('d-none');
            getCompanyDetail(e.params.data.id);
        });

        $('#select_company').select2({
            ajax: {
                url: '{{ route('company.search') }}',
                dataType: 'json',
                delay: 250,
                data: function(params) {
                    return {
                        query: params.term, // search term
                        page: params.page
                    };
                },
                processResults: function(data, params) {
                    return {
                        results: data.map(function(item) {
                            return {
                                id: item.id,
                                text: item.name,
                                img: item.img_url
                            }
                        }),
                    };
                },
                cache: true
            },
            placeholder: 'Search company...',
            minimumInputLength: 1,
            templateResult: formatResult,
            templateSelection: formatSelection,
        });

        initCountry();

        function initCountry() {
            $.ajax({
                url: '{{ route('countries.index') }}',
                dataType: 'json',
                success: function(data) {
                    $('#target_country').select2({
                        data: data.map(function(item) {
                            return {
                                id: item.id,
                                text: item.emoji + ' ' + item.name + ' ',
                            }
                        }),
                        placeholder: 'Select Target Country',
                    });
                }

            });
        }

        initIndustry();

        function initIndustry() {
            $.ajax({
                url: '{{ route('industries.index') }}',
                dataType: 'json',
                success: function(data) {
                    $('#target_industry').select2({
                        data: data.map(function(item) {
                            return {
                                id: item.id,
                                text: item.name,
                            }
                        }),
                        placeholder: 'Select Industry Type',
                    });
                    initFakeData();
                }

            });
        }

        function formatResult(result) {
            if (!result.id) return result.text;
            return $(
                '<div class="select2-result">' +
                '<img class="select2-result__image me-2 h-2rem" src="' + result.img + '">' +
                '<span class="select2-result__text">' + result.text + '</span>' +
                '</div>'
            );
        }

        function formatSelection(result) {
            if (!result.id) return result.text;
            return $(
                '<div class="select2-result">' +
                '<img class="select2-result__image me-1 h-1rem" src="' + result.img + '">' +
                '<span class="select2-result__text">' + result.text + '</span>' +
                '</div>'
            );
        }

        function getCompanyDetail(id) {
            // add swal loading here
            Swal.fire({
                text: 'Get company details...',
                showCancelButton: false,
                showConfirmButton: false,
                allowOutsideClick: false,
                allowEscapeKey: false,
                allowEnterKey: false,
                onOpen: function() {
                    Swal.showLoading();
                }
            });
            $.ajax({
                url: '{{ route('companies.get', '') }}/' + id,
                type: 'GET',
                success: function(data) {
                    //add delay of 500ms then close swal
                    setTimeout(function() {
                        Swal.close();
                        $('#company_name').text(data.name);
                        $('#company_image').attr('src', data.img_url);
                        $('#company_type').text(data.type.name);
                        $('#company_website').text(data.website);
                        $('#company_industry').text(data.industry);
                        $('#company_establish').text(data.establish);
                        $('#company_country').text(data.address.country);
                    }, 100);
                }
            });
        }

        function submitProject() {
            let keywords = $('#target_keyword').val();
            if (keywords.length < 1) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    text: 'At least a keyword is needed for project creation',
                });
                return;
            }
            Swal.fire({
                text: 'Submit project...',
                showCancelButton: false,
                showConfirmButton: false,
                allowOutsideClick: false,
                allowEscapeKey: false,
                allowEnterKey: false,
                onOpen: function() {
                    Swal.showLoading();
                }
            });
            // get target keyword values, then convert to array
            $.ajax({
                url: '{{ route('client.projects.store') }}',
                type: 'POST',
                data: {
                    _token: '{{ csrf_token() }}',
                    company_id: {{ $company->id ?? 1 }},
                    name: $('#project-name').val(),
                    description: $('#project-description').val(),
                    hub: $('#hub').val(),
                    deadline: $('#deadline').val(),
                    target_country: $('#target_country').val(),
                    target_industry: $('#sub-industry').val(),
                    target_company_size: $('#target_company_size').val(),
                    target_keyword: JSON.parse($('#target_keyword').val()).map(e => e.value),
                    communication_language: $('#communication_language').val(),
                    target_question: $('.target_question').map(function() {
                        return $(this).val();
                    }).get(),
                },
                success: function(data) {
                    //add delay of 500ms then close swal
                    setTimeout(function() {
                        Swal.close();
                        Swal.fire({
                            icon: 'success',
                            title: 'Successfully created!',
                            text: 'Successfully created your project. You will be redirect to project page.',
                            showCancelButton: false,
                            showConfirmButton: false,
                            allowOutsideClick: false,
                            allowEscapeKey: false,
                            allowEnterKey: false,
                        });
                        setTimeout(function() {
                            Swal.close();
                            window.location.href = '{{ route('client.projects.index') }}';
                        }, 1000);
                    }, 100);
                },
                error: function(data) {
                    console.log(data)
                    Swal.close();
                    Swal.fire({
                        icon: 'error',
                        title: 'Error',
                        text: data.responseJSON.message,
                    });
                }
            });
        }

        $(document).ready(function() {
            @if (!$company)
                $('.form-control').attr('disabled', true)
                $('.js-select2').select2("enable", false)
                $('.js-select2').select2("enable", false)
                $('#submitBtn').addClass('disabled').html('Please assign Organization to submit project')
            @endif
        });

        $(document).ready(function() {
            $.ajax({
                url: '{{ route('industries_expert.main') }}',
                method: 'GET',
                success: function(response) {
                    response.forEach(function(industry) {
                        $('#main-industry').append('<option value="' + industry + '">' +
                            industry + '</option>');
                    });
                }
            });
        });

        let sub_industry_classification;
        $('#main-industry').on('change', function() {
            let main = $(this).val();
            if (main === null) return;
            main = main.replaceAll('/', '_');
            $.ajax({
                url: '{{ route('industries_expert.sub', '') }}/' + main,
                method: 'GET',
                success: function(response) {
                    $('#sub-industry').empty();
                    response.forEach(function(industry) {
                        $('#sub-industry').append('<option value="' + industry.id + '">' +
                            industry.sub + '</option>');
                    });
                    if (sub_industry_classification) {
                        $('#sub-industry').val(sub_industry_classification).trigger('change');
                    }
                }
            });
        });
    </script>
@endpush
